mapper.insert:

	SELECT nextval('prgrm_sn_seq')

INSERT INTO PRGRM
( prgrm_sn
, prgrm_id
, prgrm_url
, prgrm_nm
, prgrm_level
, prgrm_ordr
, up_prgrm_id
, RLS_YN
, register_id
, reg_ymd
, updusr_id
, updt_ymd )
VALUES ( #{prgrmSn}
  	   , #{prgrmId}
	   , #{prgrmUrl}
	   , #{prgrmNm}
	   , #{prgrmLevel}
	   , #{prgrmOrdr}
	   , #{upprgrmId}
	   , #{rlsYn}
	   , #{registerId}
	   , NOW()
	   , #{updusrId}
	   , NOW() )

mapper.update:
UPDATE PRGRM
SET prgrm_id=#{prgrmId}
  , prgrm_url=#{prgrmUrl}
  , prgrm_nm=#{prgrmNm}
  , prgrm_level=#{prgrmLevel}
  , prgrm_ordr=#{prgrmOrdr}
  , up_prgrm_id=#{upprgrmId}
  , RLS_YN=#{rlsYn}
  , updusr_id=#{updusrId}
  , updt_ymd=NOW()
WHERE prgrm_sn=#{prgrmSn}
